import LeftPage from "../components/MainPageLeft.vue";
import word_cloud_data from "../assets/Sheng/item_data/word_cloud_3ddata.json";
import num_tag_data from "../assets/Sheng/item_data/locate_tag_num.json";
import speed_average_data from "../assets/Sheng/item_data/speed_average.json";
export default {
  data() {
    return {
      cur_info: {
        year: "2017",
        title: "政治",
        region: "山东",
        region_data: ""
      },
      locate_data_url: {
        河北: "https://geo.datav.aliyun.com/areas_v3/bound/130000_full.json",
        山东: "https://geo.datav.aliyun.com/areas_v3/bound/370000_full.json",
        山西: "https://geo.datav.aliyun.com/areas_v3/bound/140000_full.json",
        河南: "https://geo.datav.aliyun.com/areas_v3/bound/410000_full.json",
        辽宁: "https://geo.datav.aliyun.com/areas_v3/bound/210000_full.json"
      },
      news_value: 0,
      news_list: [{
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/874baddfbc4c43809aae929bf2a190a2.JPG"
      }, {
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/441dbcc82f714ce58365ab925ac4c783.JPG"
      }, {
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/89106ba8a3a6455e8d323ba63e307cf0.JPG"
      }],
      width: 576,
      height: 300,
      time_select_value: 0,
      tagsNum: 0,
      //标签数量
      RADIUS: 120,
      //球的半径
      speedX: Math.PI / 360 / 0.5,
      //球一帧绕x轴旋转的角度
      speedY: Math.PI / 360 / 0.5,
      //球-帧绕y轴旋转的角度
      tags: [],
      data: word_cloud_data["value"],
      timer: null
    };
  },

  computed: {
    CX() {
      //球心x坐标
      return this.width / 2;
    },

    CY() {
      //球心y坐标
      return this.height / 2;
    }

  },

  created() {
    this.initData();
  },

  mounted() {
    //this.cur_info.region = this.$route.params.region;
    console.log(this.$route.params.region); //this.set_wordcloud();

    this.runTags();
    this.set_speed_average();
    this.set_speed_tag();
    this.set_speed_region();
    this.set_num_tag();
    this.set_speed_region();
  },

  methods: {
    initData() {
      //初始化标签位置
      let tags = [];
      this.tagsNum = this.data.length;

      for (let i = 0; i < this.data.length; i++) {
        let tag = {};
        let k = -1 + (2 * (i + 1) - 1) / this.tagsNum;
        let a = Math.acos(k);
        let b = a * Math.sqrt(this.tagsNum * Math.PI); //计算标签相对于球心的角度

        tag.text = this.data[i];
        tag.x = this.CX + this.RADIUS * Math.sin(a) * Math.cos(b); //根据标签角度求出标签的x,y,z坐标

        tag.y = this.CY + this.RADIUS * Math.sin(a) * Math.sin(b);
        tag.z = this.RADIUS * Math.cos(a);
        tag.id = i; // 给标签添加id

        tag.color = "rgb(" + [Math.round(Math.random() * 254), Math.round(Math.random() * 254), Math.round(Math.random() * 254)].join(",") + ")";
        tags.push(tag); // console.log(tag);
      }

      this.tags = tags; //让vue替我们完成视图更新
    },

    // 纵向旋转
    rotateX(angleX) {
      var cos = Math.cos(angleX);
      var sin = Math.sin(angleX);

      for (let tag of this.tags) {
        var y1 = (tag.y - this.CY) * cos - tag.z * sin + this.CY;
        var z1 = tag.z * cos + (tag.y - this.CY) * sin;
        tag.y = y1;
        tag.z = z1;
      }
    },

    // 横向旋转
    rotateY(angleY) {
      var cos = Math.cos(angleY);
      var sin = Math.sin(angleY);

      for (let tag of this.tags) {
        var x1 = (tag.x - this.CX) * cos - tag.z * sin + this.CX;
        var z1 = tag.z * cos + (tag.x - this.CX) * sin;
        tag.x = x1;
        tag.z = z1;
      }
    },

    // 运动函数
    async runTags() {
      if (typeof this.timer === "number") {
        clearInterval(this.timer);
        this.timer = null;
      }

      let timer = setInterval(() => {
        this.rotateX(this.speedX);
        this.rotateY(this.speedY);
      }, 17);
      this.timer = timer;
    },

    // 监听移入事件
    listenerMove(e) {
      if (e.target.id) {
        clearInterval(this.timer);
      }
    },

    // 监听移出事件
    listenerOut(e) {
      if (e.target.id) {
        this.runTags();
      }
    },

    // 点击事件
    clickToPage() {},

    //up----------------------------------------------------------set word_cloud
    title_change(new_title) {
      //title变化后
      this.cur_info.title = new_title;

      if (num_tag_data[this.cur_info.region][this.cur_info.year][this.cur_info.title] == null) {
        alert("这里没有数据");
        return;
      }

      this.set_num_tag();
      this.set_speed_tag();
    },

    get_time_change(new_time) {
      if (num_tag_data[this.cur_info.region][this.cur_info.year][this.cur_info.title] == null) {
        alert("这里没有数据");
        return;
      } //时间变化后


      this.cur_info.year = new_time.toString();
      this.set_num_tag();
      this.set_speed_tag();
    },

    async set_num_tag() {
      // if (!this.num_tag_Chart)
      if (this.num_tag_Chart) this.num_tag_Chart.clear();else this.num_tag_Chart = this.$echarts.init(this.$refs.num_tag);
      this.num_tag_Chart.setOption({
        dataset: {
          source: num_tag_data[this.cur_info.region][this.cur_info.year][this.cur_info.title]
        },
        grid: {
          containLabel: true
        },
        xAxis: {
          name: "数量",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          type: "category",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        visualMap: {
          orient: "horizontal",
          left: "center",
          min: 0,
          max: 10,
          text: ["慢速响应", "快速响应"],
          // Map the score column to color
          dimension: "avg",
          inRange: {
            color: ["#65B581", "#FFCE34", "#FD665F"]
          },
          textStyle: {
            color: "#FFFFFF"
          }
        },
        series: [{
          type: "bar",
          encode: {
            x: "num",
            y: "sub_title"
          }
        }]
      });
    },

    async set_wordcloud() {
      //-------------------------set wordcloud
      if (!this.word_cloud_Chart) this.word_cloud_Chart = this.$echarts.init(this.$refs.word_cloud);
      var maskImage = new Image();
      maskImage.src = word_cloud_data.image;
      this.word_cloud_Chart.setOption({
        series: [{
          type: "wordCloud",
          shape: "circle",
          keepAspect: false,
          left: "center",
          top: "center",
          width: "70%",
          height: "70%",
          right: null,
          bottom: null,
          //maskImage:maskImage,
          sizeRange: [12, 30],
          rotationRange: [-45, 45],
          rotationStep: 90,
          // 词间距，数值越小，间距越小，这里间距太小的话，会出现大词把小词套住的情况，比如一个大的口字，中间会有比较大的空隙，这时候他会把一些很小的字放在口字里面，这样的话，鼠标就无法选中里面的那个小字
          gridSize: 2,
          // 允许词太大的时候，超出画布的范围
          drawOutOfBound: false,
          // 布局的时候是否有动画
          layoutAnimation: false,
          textStyle: {
            fontWeight: "bold",
            color: function () {
              return "rgb(" + [Math.round(Math.random() * 254), Math.round(Math.random() * 254), Math.round(Math.random() * 254)].join(",") + ")";
            }
          },
          emphasis: {
            focus: "self",
            textStyle: {
              textShadowBlur: 10,
              textShadowColor: "#333"
            }
          },
          // Data is an array. Each array item must have name and value property.
          data: word_cloud_data.value
        }]
      });
    },

    async set_speed_average() {
      //--------------------set speed_average
      //-------------------------------------------------------------set scar
      let scar_data = speed_average_data["scar"][this.cur_info.region];
      let scar_series = [];

      for (let title_name in scar_data) {
        scar_series.push({
          type: "line",
          id: title_name,
          dataGroupId: title_name,
          universalTransition: {
            enabled: true,
            delay: function (idx, count) {
              return Math.random() * 400;
            }
          },
          data: scar_data[title_name]
        });
      }

      let speed_region_average_scatterOption = {
        xAxis: {
          scale: true,
          name: "年份",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          scale: true,
          name: "响应时间",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        series: scar_series
      }; //-------------------------------------------------------设置bar

      let bar_data = speed_average_data["bar"][this.cur_info.region];
      let bar_series = [],
          bar_x_data = [];

      for (let title_name in bar_data) {
        bar_x_data.push(title_name);
        bar_series.push({
          value: bar_data[title_name],
          groupId: title_name
        });
      }

      let bar_key = bar_x_data;
      let speed_region_average_barOption = {
        xAxis: {
          type: "category",
          data: bar_x_data,
          name: "类别",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          name: "响应时间",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        series: [{
          type: "bar",
          id: "total",
          data: bar_series,
          universalTransition: {
            enabled: true,
            seriesKey: bar_key,
            delay: function (idx, count) {
              return Math.random() * 400;
            }
          }
        }]
      };
      if (!this.speed_average_Chart) this.speed_average_Chart = this.$echarts.init(this.$refs.speed_average);
      let currentOption = speed_region_average_scatterOption,
          that = this;
      setInterval(function () {
        currentOption = currentOption === speed_region_average_scatterOption ? speed_region_average_barOption : speed_region_average_scatterOption;
        that.speed_average_Chart.setOption(currentOption, true);
      }, 2000);
    },

    async set_speed_region() {
      //--------------------set speed_region
      await this.$axios({
        url: this.locate_data_url[this.cur_info.region],
        method: "get"
      }).then(res => {
        this.cur_info.region_data = res.data;
      });
      this.$echarts.registerMap(this.cur_info.region, this.cur_info.region_data);
      let _data = this.cur_info.region_data.features,
          scar_data = [];

      for (let i in _data) {
        let x = _data[i].properties.center[0],
            y = _data[i].properties.center[1],
            z = _data[i].properties.name;
        scar_data.push([x, y, z]);
      }

      this.speed_region_Chart = this.$echarts.init(this.$refs.speed_region);
      let speed_region_option = {
        geo: {
          map: this.cur_info.region,
          z: 2,
          aspectScale: 1,
          layoutCenter: ["50%", "50%"],
          //地图位置
          zoom: 1,
          //当前视角的缩放比例
          scaleLimit: {
            //滚轮缩放的极限控制
            min: 1,
            max: 2
          },
          label: {
            normal: {
              show: true,
              textStyle: {
                color: "#FFFFFF",
                fontSize: 18
              }
            },
            emphasis: {
              show: true,
              textStyle: {
                color: "#FFFFFF",
                fontSize: 18
              }
            }
          },
          itemStyle: {
            normal: {
              shadowColor: "rgba(21,55,112,255)",
              shadowOffsetX: 10,
              shadowOffsetY: 10,
              areaColor: "rgba(24,48,82,255)",
              borderColor: "rgba(84,113,166)",
              borderWidth: 1.5
            },
            emphasis: {
              areaColor: "rgba(7,69,162)"
            }
          }
        },
        series: [{
          type: "map",
          mapType: this.cur_info.region,
          aspectScale: 1,
          zoom: 1,
          z: 1,
          layoutCenter: ["50%", "50%"],
          //地图位置
          label: {
            normal: {
              show: false
            },
            emphasis: {
              show: false
            }
          },
          itemStyle: {
            normal: {
              shadowColor: "rgba(22,57,89,255)",
              shadowOffsetX: 20,
              shadowOffsetY: 20,
              //areaColor: "rgba(22,57,89,255)",
              borderColor: "#4a75ff",
              borderWidth: 7
            },
            emphasis: {
              areaColor: "rgba(22,57,89,0)"
            }
          }
        }, {
          show: true,
          type: "effectScatter",
          rippleEffect: {
            // 涟漪特效相关配置。
            scale: 4 // 控制涟漪大小

          },
          coordinateSystem: "geo",
          // series坐标系类型
          data: scar_data
        }]
      };
      this.speed_region_Chart.setOption(speed_region_option);
    },

    async set_speed_tag() {
      //--------------------------set speedTag
      if (this.speed_tag_Chart) this.speed_tag_Chart.clear();else this.speed_tag_Chart = this.$echarts.init(this.$refs.speed_tag);
      this.speed_tag_Chart.clear();
      this.speed_tag_Chart.setOption({
        dataset: {
          source: num_tag_data[this.cur_info.region][this.cur_info.year][this.cur_info.title]
        },
        polar: {
          radius: [30, "70%"]
        },
        radiusAxis: {
          type: "category",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          show: false,
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        angleAxis: {
          startAngle: 75,
          name: "响应时间",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        tooltip: {},
        series: {
          type: "bar",
          coordinateSystem: "polar",
          encode: {
            radius: 0,
            angle: 1
          },
          label: {
            show: false,
            position: "middle"
          },
          itemStyle: {
            color: function (params) {
              let colors = ["#4587E7", "#35AB33", "#F5AD1D", "#ff7f50", "#da70d6", "#32cd32", "#6495ed", "#F3FA00"];
              return colors[params.dataIndex];
            }
          }
        }
      });
    }

  },

  beforeDestroy() {
    if (this.speed_region_Chart) {
      this.speed_region_Chart.dispose();
      this.speed_region_Chart = null;
    }

    if (this.detail_chart) {
      this.detail_chart.dispose();
      this.detail_chart = null;
    }

    if (this.word_cloud_Chart) {
      this.word_cloud_Chart.dispose();
      this.word_cloud_Chart = null;
    }

    if (this.num_tag_Chart) {
      this.num_tag_Chart.dispose();
      this.num_tag_Chart = null;
    }

    if (this.speed_average_Chart) {
      this.speed_average_Chart.dispose();
      this.speed_average_Chart = null;
    }
  },

  components: {
    LeftPage
  }
};
