import LeftPage from "../components/MainPageLeft.vue";
import shandong from "../assets/map/province/shandong.json";
import test_data from "../assets/Sheng/item_data/test_data.json";
import word_cloud_data from "../assets/Sheng/item_data/word_cloud.json";
import num_tag_data from "../assets/Sheng/item_data/locate_tag_num.json";
export default {
  data: function data() {
    return {
      cur_info: {
        year: '2017',
        title: '政治',
        region: '河北'
      },
      news_value: 0,
      news_list: [{
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/874baddfbc4c43809aae929bf2a190a2.JPG"
      }, {
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/441dbcc82f714ce58365ab925ac4c783.JPG"
      }, {
        url: "http://www.gov.cn/xinwen/2022-05/01/5688367/images/89106ba8a3a6455e8d323ba63e307cf0.JPG"
      }],
      speed_region_option: {
        geo: {
          map: "山东",
          aspectScale: 1,
          layoutCenter: ["50%", "50%"],
          //地图位置
          layoutSize: "150%",
          label: {
            normal: {
              show: false
            },
            emphasis: {
              show: false
            }
          },
          itemStyle: {
            normal: {
              shadowColor: "rgba(22,57,89,255)",
              shadowOffsetX: 20,
              shadowOffsetY: 20,
              //areaColor: "rgba(22,57,89,255)",
              borderColor: "#4a75ff",
              borderWidth: 7
            },
            emphasis: {
              areaColor: "rgba(22,57,89,0)"
            }
          }
        },
        series: [// 常规地图
        {
          type: "map",
          mapType: "山东",
          aspectScale: 1,
          layoutCenter: ["50%", "50%"],
          //地图位置
          layoutSize: "150%",
          zoom: 1,
          //当前视角的缩放比例
          // roam: true, //是否开启平游或缩放
          scaleLimit: {
            //滚轮缩放的极限控制
            min: 1,
            max: 2
          },
          label: {
            normal: {
              show: true,
              textStyle: {
                color: "#FFFFFF",
                fontSize: 18
              }
            },
            emphasis: {
              show: true,
              textStyle: {
                color: "#FFFFFF",
                fontSize: 18
              }
            }
          },
          itemStyle: {
            normal: {
              shadowColor: "rgba(21,55,112,255)",
              shadowOffsetX: 10,
              shadowOffsetY: 10,
              areaColor: "rgba(24,48,82,255)",
              borderColor: "rgba(84,113,166)",
              borderWidth: 1.5
            },
            emphasis: {
              areaColor: "rgba(7,69,162)"
            }
          }
        }]
      },
      time_select_value: 0
    };
  },
  mounted: function mounted() {
    this.set_speed_region();
    this.set_wordcloud();
    this.set_speed_average();
    this.set_speed_tag();
    this.set_speed_region();
    this.set_num_tag();
  },
  methods: {
    title_change: function title_change(new_title) {
      //title变化后
      this.cur_info.title = new_title;
      this.set_num_tag();
    },
    get_time_change: function get_time_change(new_time) {
      //时间变化后
      this.cur_info.year = new_time.toString();
      this.set_num_tag();
    },
    calculateAverage: function calculateAverage(data, dim) {
      var total = 0;

      for (var i = 0; i < data.length; i++) {
        total += data[i][dim];
      }

      return total /= data.length;
    },
    set_num_tag: function set_num_tag() {
      var Chart = this.$echarts.init(this.$refs.num_tag);
      Chart.setOption({
        dataset: {
          source: num_tag_data[this.cur_info.region][this.cur_info.year][this.cur_info.title]
        },
        grid: {
          containLabel: true
        },
        xAxis: {
          name: "数量",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          type: "category",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        visualMap: {
          orient: "horizontal",
          left: "center",
          min: 0,
          max: 10,
          text: ["慢速响应", "快速响应"],
          // Map the score column to color
          dimension: 'avg',
          inRange: {
            color: ["#65B581", "#FFCE34", "#FD665F"]
          },
          textStyle: {
            color: "#FFFFFF"
          }
        },
        series: [{
          type: "bar",
          encode: {
            x: "num",
            y: "sub_title"
          }
        }]
      });
    },
    set_wordcloud: function set_wordcloud() {
      //-------------------------set wordcloud
      var word_cloud_Chart = this.$echarts.init(this.$refs.word_cloud);
      var maskImage = new Image();
      maskImage.src = word_cloud_data.image;
      word_cloud_Chart.setOption({
        series: [{
          type: "wordCloud",
          shape: "circle",
          keepAspect: false,
          left: "center",
          top: "center",
          width: "70%",
          height: "70%",
          right: null,
          bottom: null,
          //maskImage:maskImage,
          sizeRange: [12, 30],
          rotationRange: [-45, 45],
          rotationStep: 90,
          // 词间距，数值越小，间距越小，这里间距太小的话，会出现大词把小词套住的情况，比如一个大的口字，中间会有比较大的空隙，这时候他会把一些很小的字放在口字里面，这样的话，鼠标就无法选中里面的那个小字
          gridSize: 2,
          // 允许词太大的时候，超出画布的范围
          drawOutOfBound: false,
          // 布局的时候是否有动画
          layoutAnimation: false,
          textStyle: {
            fontWeight: "bold",
            color: function color() {
              return "rgb(" + [Math.round(Math.random() * 254), Math.round(Math.random() * 254), Math.round(Math.random() * 254)].join(",") + ")";
            }
          },
          emphasis: {
            focus: "self",
            textStyle: {
              textShadowBlur: 10,
              textShadowColor: "#333"
            }
          },
          // Data is an array. Each array item must have name and value property.
          data: word_cloud_data.value
        }]
      });
    },
    set_speed_average: function set_speed_average() {
      //--------------------set speed_average
      var speed_region_average_scatterOption = {
        dataset: {
          source: word_cloud_data["femaleData"]
        },
        xAxis: {
          scale: true,
          name: "年份",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          scale: true,
          name: "速度",
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        series: [{
          type: "scatter",
          id: "female",
          //dataGroupId: "female",
          universalTransition: {
            enabled: true,
            delay: function delay(idx, count) {
              return Math.random() * 400;
            }
          },
          data: test_data.femaleData
        }, {
          type: "scatter",
          id: "male",
          dataGroupId: "male",
          universalTransition: {
            enabled: true,
            delay: function delay(idx, count) {
              return Math.random() * 400;
            }
          },
          data: test_data.maleDeta
        }]
      };
      var speed_region_average_barOption = {
        xAxis: {
          type: "category",
          data: ["Female", "Male"],
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        yAxis: {
          nameTextStyle: {
            color: "rgba(255,255,255)"
          },
          axisLabel: {
            textStyle: {
              color: "rgba(255,255,255)"
            }
          }
        },
        series: [{
          type: "bar",
          id: "total",
          data: [{
            value: 200,
            groupId: "male"
          }, {
            value: 300,
            groupId: "female"
          }],
          universalTransition: {
            enabled: true,
            seriesKey: ["female", "male"],
            delay: function delay(idx, count) {
              return Math.random() * 400;
            }
          }
        }]
      };
      var speed_average_Chart = this.$echarts.init(this.$refs.speed_average);
      var currentOption = speed_region_average_scatterOption;
      setInterval(function () {
        currentOption = currentOption === speed_region_average_scatterOption ? speed_region_average_barOption : speed_region_average_scatterOption;
        speed_average_Chart.setOption(currentOption, true);
      }, 2000);
    },
    set_speed_region: function set_speed_region() {
      //--------------------set speed_region
      this.$echarts.registerMap("山东", shandong);
      this.speed_region_Chart = this.$echarts.init(this.$refs.speed_region);
      this.speed_region_Chart.setOption(this.speed_region_option);
    },
    set_speed_tag: function set_speed_tag() {
      //--------------------------set speedTag
      var speed_tag_Chart = this.$echarts.init(this.$refs.speed_tag);
      speed_tag_Chart.setOption({
        polar: {
          radius: [30, "70%"]
        },
        angleAxis: {
          max: 4,
          startAngle: 75
        },
        radiusAxis: {
          type: "category",
          data: ["a", "b", "c", "d"]
        },
        tooltip: {},
        series: {
          type: "bar",
          data: [2, 1.2, 2.4, 3.6],
          coordinateSystem: "polar",
          label: {
            show: true,
            position: "middle",
            formatter: "{b}: {c}"
          }
        }
      });
    }
  },
  components: {
    LeftPage: LeftPage
  }
};
